                'ingredients' => '[{"name":"FILLING","quantity":"","subIngredients":"","_id":"5b10244426f4151be8d41fd2"},{"name":"big onions, diced","quantity":"3","subIngredients":"","_id":"5b10244426f4151be8d41fd1"},{"name":"cup olive oil","quantity":"\\u00bd","subIngredients":"","_id":"5b10244426f4151be8d41fd0"},{"name":"medium tomatoes, diced","quantity":"2","subIngredients":"","_id":"5b10244426f4151be8d41fcf"},{"name":"sweet red pepper, diced","quantity":"\\u00bd","subIngredients":"","_id":"5b10244426f4151be8d41fce"},{"name":"tablespoon hot pepper paste","quantity":"1","subIngredients":"","_id":"5b10244426f4151be8d41fcd"},{"name":"Salt and pepper","quantity":"","subIngredients":"","_id":"5b10244426f4151be8d41fcc"},{"name":"DOUGH","quantity":"","subIngredients":"","_id":"5b10244426f4151be8d41fcb"},{"name":"cups flour","quantity":"3","subIngredients":"","_id":"5b10244426f4151be8d41fca"},{"name":"tablespoon powdered milk","quantity":"1","subIngredients":"","_id":"5b10244426f4151be8d41fc9"},{"name":"teaspoon sugar","quantity":"\\u00bd","subIngredients":"","_id":"5b10244426f4151be8d41fc8"},{"name":"teaspoon salt","quantity":"\\u00bd","subIngredients":"","_id":"5b10244426f4151be8d41fc7"},{"name":"tablespoons olive oil","quantity":"2","subIngredients":"","_id":"5b10244426f4151be8d41fc6"},{"name":"teaspoon instant yeast","quantity":"1","subIngredients":"","_id":"5b10244426f4151be8d41fc5"},{"name":"cups water","quantity":"\\u00bc","subIngredients":"","_id":"5b10244426f4151be8d41fc4"}]',
                'directions_legacy' => '<ul>
<li>FILLING</li>
<li>Combine all the ingredients together and keep aside.</li>
<li>Mix the flour, powdered milk, salt and sugar in a bowl. Make a hole in the center and pour in the instant yeast and olive oil. Next, add gradually water as needed to form dough.</li>
<li>Put the dough on a floured work surface. Knead it about 10 minutes or until it becomes smooth.</li>
<li>Divide the dough into four pieces.</li>
<li>Cover them with nylon sheet and kitchen cloth. Leave to rest for 2 hours.</li>
<li>MANKOUCHEH</li>
<li>Roll out the dough on a floured table until it becomes thin.</li>
<li>Cut it with a rounded cookie cutter in order to create circles.</li>
<li>Add some of the onion mixture on each piece of dough.</li>
<li>Place them in an oiled baking tray.</li>
<li>Bake in the oven until golden.</li>
<li>45 to 50 pieces</li>
</ul>',
                'ingredients_legacy' => '<ul>
<li>FILLING</li>
<li>3 big onions, diced</li>
<li>½ cup olive oil</li>
<li>2 medium tomatoes, diced</li>
<li>½ sweet red pepper, diced</li>
<li>1 tablespoon hot pepper paste</li>
<li>Salt and pepper</li>
<li>DOUGH</li>
<li>3 cups flour</li>
<li>1 tablespoon powdered milk</li>
<li>½ teaspoon sugar</li>
<li>½ teaspoon salt</li>
<li>2 tablespoons olive oil</li>
<li>1 teaspoon instant yeast</li>
<li>¼ cups water</li>
</ul>',
