                'ingredients' => '[{"name":"DOUGH","quantity":"bold","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e8c"},{"name":"flour","quantity":"2 cups","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e8b"},{"name":"instant yeast","quantity":"1 teaspoon","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e8a"},{"name":"baking powder","quantity":"1 teaspoon","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e89"},{"name":"sugar","quantity":"\\u00bc teaspoon","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e88"},{"name":"salt","quantity":"\\u00bc teaspoon","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e87"},{"name":"olive oil","quantity":"2 tablespoons","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e86"},{"name":"powdered milk","quantity":"2 tablespoons","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e85"},{"name":"warm water","quantity":"\\u00bd to \\u00be cup","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e84"},{"name":"","quantity":"","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e83"},{"name":"FILLING","quantity":"bold","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e82"},{"name":"2","quantity":"tomatoes, cut into thin and round pieces","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e81"},{"name":"2","quantity":"tomatoes, diced","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e80"},{"name":"tomato sauce","quantity":"\\u00bd  cup","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e7f"},{"name":"mozzarella cheese, sliced","quantity":"250 grams","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e7e"},{"name":"olive oil","quantity":"2 tablespoons","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e7d"},{"name":"fresh basil, chopped","quantity":"2 tablespoons","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e7c"},{"name":"salt and pepper","quantity":"","subIngredients":"","_id":"5aec5f05825cbd0f5d3a7e7b"}]',
                'directions_legacy' => '<ul><li>DOUGH:</li><li>Mix the flour, yeast, salt and sugar in a bowl.</li><li>Then, add the powdered milk and baking powder. Make a hole in the center and pour in the oil. Stir in water as needed to form a soft dough.</li><li>If the dough is too stiff, add some water.</li><li>Put the dough into an oiled bowl and coat it with oil. Cover and let it rest for 2 hours.</li><li></li><li>PIZZA:</li><li>Roll out the dough to form a flat circle.</li><li>Place the dough in a round tray</li><li>Bake it in the oven for 8 minutes</li><li>Place half of the mozzarella slices on the prepared dough. Spread over the tomato sauce. Then, add the diced tomatoes and the round tomatoes over. Next, add the remaining mozzarella. Sprinkle over the basil, olive oil, salt and pepper.</li><li>Bake in the oven until the cheese melt.</li></ul>',
