                'directions' => '[{"direction":"Roll the dough between two nylon sheets.","_id":"5aec53f2825cbd0f5d3a7e46"},{"direction":"Place the dough in a pie pan and cover it with wax paper; spread over some dry beans and bake in the oven for 10 minutes, then remove the paper and beans and bake until golden","_id":"5aec53f2825cbd0f5d3a7e45"},{"direction":"Combine the pesto sauce with tomato sauce and spread over the dough. Then, add the shredded cheese and place the buffalo mozzarella over. Next, sprinkle the diced tomato in between. Sprinkle over the dried basil.","_id":"5aec53f2825cbd0f5d3a7e44"},{"direction":"Bake in the oven until the cheese melts.","_id":"5aec53f2825cbd0f5d3a7e43"},{"direction":"Decorate with the basil leaves and black olives.","_id":"5aec53f2825cbd0f5d3a7e42"}]',
                'ingredients' => '[{"name":"shortcrust dough","quantity":"1","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e4f"},{"name":"pesto sauce","quantity":"\\u00bd jar","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e4e"},{"name":"tomato sauce","quantity":"\\u00be cup","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e4d"},{"name":"shredded mozzarella cheese","quantity":"150 g","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e4c"},{"name":"small buffalo mozzarella","quantity":"12 fresh","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e4b"},{"name":"tomatoes, diced","quantity":"4","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e4a"},{"name":"dried basil","quantity":"1 tablespoon","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e49"},{"name":"basil leaves","quantity":"10","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e48"},{"name":"seedless black olives, chopped","quantity":"10","subIngredients":"","_id":"5aec53f2825cbd0f5d3a7e47"}]',
                'directions_legacy' => '<ul><li>Roll the dough between two nylon sheets.</li><li>Place the dough in a pie pan and cover it with wax paper; spread over some dry beans and bake in the oven for 10 minutes, then remove the paper and beans and bake until golden</li><li>Combine the pesto sauce with tomato sauce and spread over the dough. Then, add the shredded cheese and place the buffalo mozzarella over. Next, sprinkle the diced tomato in between. Sprinkle over the dried basil.</li><li>Bake in the oven until the cheese melts.</li><li>Decorate with the basil leaves and black olives.</li></ul>',
