            'ingredients' => '[{"name":"MEAT","quantity":"bold","subIngredients":"","_id":"5a9803a8018aea2f8304d60b"},{"name":"minced meat (sheep)","quantity":"600    Grams","subIngredients":"","_id":"5a9803a8018aea2f8304d60a"},{"name":"Medium tomatoes grated","quantity":"4","subIngredients":"","_id":"5a9803a8018aea2f8304d609"},{"name":"Medium onions","quantity":"3","subIngredients":"","_id":"5a9803a8018aea2f8304d608"},{"name":"\\u00bd    Fresh green pepper","quantity":"","subIngredients":"","_id":"5a9803a8018aea2f8304d607"},{"name":"Garlic cloves","quantity":"4","subIngredients":"","_id":"5a9803a8018aea2f8304d606"},{"name":"chopped Parsley","quantity":"1 \\u00bd    Cups","subIngredients":"","_id":"5a9803a8018aea2f8304d605"},{"name":"Tomato Paste","quantity":"2    Tablespoons","subIngredients":"","_id":"5a9803a8018aea2f8304d604"},{"name":"Black Pepper","quantity":"1    Teaspoon","subIngredients":"","_id":"5a9803a8018aea2f8304d603"},{"name":"Sweet Pepper","quantity":"\\u00bd     Teaspoon","subIngredients":"","_id":"5a9803a8018aea2f8304d602"},{"name":"Squeezed Lemon","quantity":"1","subIngredients":"","_id":"5a9803a8018aea2f8304d601"},{"name":"Hot Pepper","quantity":"\\u00bc    Teaspoon","subIngredients":"","_id":"5a9803a8018aea2f8304d600"},{"name":"Salt to taste","quantity":"","subIngredients":"","_id":"5a9803a8018aea2f8304d5ff"},{"name":"","quantity":"","subIngredients":"","_id":"5a9803a8018aea2f8304d5fe"},{"name":"DOUGH","quantity":"bold","subIngredients":"","_id":"5a9803a8018aea2f8304d5fd"},{"name":"Flour","quantity":"3        Cups","subIngredients":"","_id":"5a9803a8018aea2f8304d5fc"},{"name":"instant Yeast","quantity":"1        Teaspoon","subIngredients":"","_id":"5a9803a8018aea2f8304d5fb"},{"name":"Egg","quantity":"1","subIngredients":"","_id":"5a9803a8018aea2f8304d5fa"},{"name":"Oil","quantity":"\\u00bc       Cup","subIngredients":"","_id":"5a9803a8018aea2f8304d5f9"},{"name":"Sugar","quantity":"\\u00bd        Teaspoon","subIngredients":"","_id":"5a9803a8018aea