            'ingredients' => '[{"name":"Rice","quantity":"bold","subIngredients":"","_id":"5a4e2e482094371c8244a33b"},{"name":"","quantity":"","subIngredients":"","_id":"5a4e2e482094371c8244a33a"},{"name":"","quantity":"","subIngredients":"","_id":"5a4e2e482094371c8244a339"},{"name":"Big Onion, cut into small cubes","quantity":"1","subIngredients":"","_id":"5a4e2e482094371c8244a338"},{"name":"Sweet corn","quantity":"200 g","subIngredients":"","_id":"5a4e2e482094371c8244a337"},{"name":"Uncle Ben\\u2019s rice","quantity":"1        Cup","subIngredients":"","_id":"5a4e2e482094371c8244a336"},{"name":"Water","quantity":"1 \\u00bc    Cups","subIngredients":"","_id":"5a4e2e482094371c8244a335"},{"name":"Salt and Ground Black Pepper","quantity":"","subIngredients":"","_id":"5a4e2e482094371c8244a334"},{"name":"","quantity":"","subIngredients":"","_id":"5a4e2e482094371c8244a333"},{"name":"WHITE SAUCE","quantity":"bold","subIngredients":"","_id":"5a4e2e482094371c8244a332"},{"name":"Butter","quantity":"20 g","subIngredients":"","_id":"5a4e2e482094371c8244a331"},{"name":"Flour","quantity":"2   Tablespoons","subIngredients":"","_id":"5a4e2e482094371c8244a330"},{"name":"Skimmed Milk","quantity":"6    Cups","subIngredients":"","_id":"5a4e2e482094371c8244a32f"},{"name":"Fresh Cream","quantity":"300 ml","subIngredients":"","_id":"5a4e2e482094371c8244a32e"},{"name":"white pepper","quantity":"\\u00bd      Teaspoon","subIngredients":"","_id":"5a4e2e482094371c8244a32d"},{"name":"Nutmeg","quantity":"\\u00bc      Teaspoon","subIngredients":"","_id":"5a4e2e482094371c8244a32c"},{"name":"Salt to taste","quantity":"","subIngredients":"","_id":"5a4e2e482094371c8244a32b"},{"name":"","quantity":"","subIngredients":"","_id":"5a4e2e482094371c8244a32a"},{"name":"CHICKEN","quantity":"bold","subIngredients":"","_id":"5a4e2e482094371c8244a329"},{"name":"Grilled chicken breast, cut into pieces","quantity":"1","subIngredients":"","_id":"5a4e2e482094371c8244a328"},{"name":"Sliced Mushrooms (canned)","quantity":"200 g","subIngredients":"","_id":"5a4e2e482094371c8244a327"},{"name":"Slices Ham, cut into small cubes","quantity":"4","subIngredients":"","_id":"5a4e2e482094371c8244a326"},{"name":"Mozzarella Cheese","quantity":"100 g","subIngredients":"","_id":"5a4e2e482094371c8244a325"},{"name":"Cheddar Cheese","quantity":"50  g","subIngredients":"","_id":"5a4e2e482094371c8244a324"}]',
                'directions_legacy' => 'Rice:

&nbsp;
<ul>
<li>Soak the rice in water for half an hour before cooking.</li>
<li>Pour corn oil in a casserole and fry the onions until golden. Add the corn and cook for 5 minutes. Then, add the rice, water, salt and black pepper. Once it starts boiling, reduce the heat, and cover the casserole and cook until all of the water is evaporated.</li>
</ul>
&nbsp;

White sauce:

&nbsp;
<ul>
<li>In another pan, melt the butter and flour. Take the pan off the heat, then add the milk, cream, salt, nutmeg and white pepper and stir on low heat until sauce thickens. Set aside</li>
</ul>
&nbsp;

Gratin:

&nbsp;
<ul>
<li>Combine the rice with chicken, mushrooms and half of the mozzarella cheese.</li>
<li>Wipe the Pyrex with one tablespoon of oil. Pour the rice mixture, then the white sauce and mix lightly. Sprinkle the rest of the mozzarella and the cheddar on top.</li>
<li>Bake in the oven for 10 minutes or until the cheese melts.</li>
</ul>',
                'ingredients_legacy' => 'Rice:

&nbsp;
<ul>
<li>1 Big onion, cut into small cubes</li>
<li>200g Sweet corn</li>
<li>1 Cup Uncle Ben’s rice</li>
<li>1 ¼ Cups water</li>
<li>Salt and ground black pepper</li>
</ul>
&nbsp;

White sauce:

&nbsp;
<ul>
<li>20g Butter</li>
<li>2 Tablespoons Flour</li>
<li>6 Cups skimmed milk</li>
<li>300ml Fresh cream</li>
<li>½ Teaspoon white pepper</li>
<li>¼ Teaspoon nutmeg</li>
<li>Salt to taste</li>
</ul>
&nbsp;

Chicken:

&nbsp;
<ul>
<li>1 Grilled chicken breast, cut into pieces</li>
<li>200 g Sliced mushrooms (canned)</li>
<li>100 g Mozzarella Cheese</li>
<li>50 g Cheddar Cheese</li>
</ul>',
