                'ingredients' => '[{"name":"DOUGH","quantity":"bold","subIngredients":"","_id":"5a3a7932e4d9de051cb76373"},{"name":"flour","quantity":"1 \\u00bd     Cups","subIngredients":"","_id":"5a3a7932e4d9de051cb76372"},{"name":"instant yeast","quantity":"1         Teaspoon","subIngredients":"","_id":"5a3a7932e4d9de051cb76371"},{"name":"sugar","quantity":"1          Pinch","subIngredients":"","_id":"5a3a7932e4d9de051cb76370"},{"name":"salt","quantity":"1 Pinch","subIngredients":"","_id":"5a3a7932e4d9de051cb7636f"},{"name":"olive oil","quantity":"1 Tablespoon","subIngredients":"","_id":"5a3a7932e4d9de051cb7636e"},{"name":"powdered milk","quantity":"2          Tablespoons","subIngredients":"","_id":"5a3a7932e4d9de051cb7636d"},{"name":"warm water","quantity":"\\u00bd         Cup","subIngredients":"","_id":"5a3a7932e4d9de051cb7636c"},{"name":"","quantity":"","subIngredients":"","_id":"5a3a7932e4d9de051cb7636b"},{"name":"FILLING","quantity":"bold","subIngredients":"","_id":"5a3a7932e4d9de051cb7636a"},{"name":"grated mozzarella","quantity":"100     Grams","subIngredients":"","_id":"5a3a7932e4d9de051cb76369"},{"name":"Pack feta cheese, cut into cubes","quantity":"1","subIngredients":"","_id":"5a3a7932e4d9de051cb76368"},{"name":"Cherry tomatoes, cut into halves","quantity":"20","subIngredients":"","_id":"5a3a7932e4d9de051cb76367"},{"name":"Medium red onion, diced","quantity":"1","subIngredients":"","_id":"5a3a7932e4d9de051cb76366"},{"name":"Seedless black olives,","quantity":"10","subIngredients":"","_id":"5a3a7932e4d9de051cb76365"},{"name":"olive oil","quantity":"2        Tablespoons","subIngredients":"","_id":"5a3a7932e4d9de051cb76364"},{"name":"Sweet green pepper, sliced","quantity":"\\u00bd","subIngredients":"","_id":"5a3a7932e4d9de051cb76363"},{"name":"chopped mint","quantity":"\\u00bd        Cup","subIngredients":"","_id":"5a3a7932e4d9de051cb76362"},{"name":"Dried Oregano","quantity":"","subIngredients":"","_id":"5a3a7932e4d9de051cb76361"},{"name":"Powdered Garlic","quantity":"","subIngredients":"","_id":"5a3a7932e4d9de051cb76360"}]',
                'directions_legacy' => 'Dough:

&nbsp;
<ul>
<li>Mix the flour, yeast, salt and sugar in a bowl. Then, add the powdered milk. Make a hole in the middle, and pour the oil into it. Stir and add water as needed to form a soft dough.</li>
<li>If the dough is too stiff, add more water. Put the dough into an oiled bowl and let it rest for 2 hours.</li>
<li>Roll out the dough and place it in a baking tray.</li>
<li>Bake in the oven until it changes color.</li>
</ul>
&nbsp;

Pizza:

&nbsp;
<ul>
<li>Put one layer of mozzarella cheese over the dough. Sprinkle with dry oregano and powdered garlic. Then, add the feta cubes, and place the slices of cherry tomatoes between them. Lastly, add the onions, pepper and the black olives.</li>
<li>Bake in the oven for 10 to 20 minutes.</li>
<li>Before serving spread the chopped fresh mint and olive oil on top.</li>
</ul>',
                'ingredients_legacy' => 'Dough:

&nbsp;
<ul>
<li>1 ½ Cups flour</li>
<li>1 Teaspoon instant yeast</li>
<li>1 Pinch sugar</li>
<li>1 Pinch salt</li>
<li>1 Tablespoon olive oil</li>
<li>2 Tablespoons powdered milk</li>
<li>½ Cup warm water</li>
</ul>
&nbsp;

Filling:

&nbsp;
<ul>
<li>100 Grams grated mozzarella</li>
<li>1 Pack feta cheese, cut into cubes</li>
<li>20 Cherry tomatoes, cut into halves</li>
<li>1 Medium red onion, diced</li>
<li>10 Seedless black olives,</li>
<li>2 Tablespoons olive oil</li>
<li>½ Sweet green pepper, sliced</li>
<li>½ Cup chopped mint</li>
<li>Dried oregano</li>
<li>Powdered garlic</li>
</ul>',
