                'ingredients' => '[{"name":"CAKE","quantity":"bold","subIngredients":"","_id":"5a0468503c664e50f7eab4c8"},{"name":"Eggs","quantity":"8","subIngredients":"","_id":"5a0468503c664e50f7eab4c7"},{"name":"sugar","quantity":"2    Cups","_id":"5a0468503c664e50f7eab4c6"},{"name":"flour","quantity":"2 Cups","subIngredients":"","_id":"5a0468503c664e50f7eab4c5"},{"name":"rum","quantity":"1 teaspoon","subIngredients":"","_id":"5a0468503c664e50f7eab4c4"},{"name":"water","quantity":"3  Tablespoons","subIngredients":"","_id":"5a0468503c664e50f7eab4c3"},{"name":"lemon juice","quantity":"3 Tablespoons","subIngredients":"","_id":"5a0468503c664e50f7eab4c2"},{"name":"baking powder","quantity":"3  Teaspoons","subIngredients":"","_id":"5a0468503c664e50f7eab4c1"},{"name":"grated lemon rind","quantity":"1  Teaspoon","subIngredients":"","_id":"5a0468503c664e50f7eab4c0"},{"name":"vanilla","quantity":"1 Pinch","subIngredients":"","_id":"5a0468503c664e50f7eab4bf"},{"name":"cocoa","quantity":"\\u00bd Cup","subIngredients":"","_id":"5a0468503c664e50f7eab4be"},{"name":"","quantity":"","subIngredients":"","_id":"5a0468503c664e50f7eab4bd"},{"name":"FILLING CREAM","quantity":"bold","subIngredients":"","_id":"5a0468503c664e50f7eab4bc"},{"name":"Egg","quantity":"1","subIngredients":"","_id":"5a0468503c664e50f7eab4bb"},{"name":"sugar","quantity":"\\u00bd  Cup","subIngredients":"","_id":"5a0468503c664e50f7eab4ba"},{"name":"flour","quantity":"2 Tablespoons","subIngredients":"","_id":"5a0468503c664e50f7eab4b9"},{"name":"liquid milk","quantity":"1 Cup","subIngredients":"","_id":"5a0468503c664e50f7eab4b8"},{"name":"vanilla","quantity":"1 Pinch","subIngredients":"","_id":"5a0468503c664e50f7eab4b7"},{"name":"grated lemon rind","quantity":"1 Teaspoon","subIngredients":"","_id":"5a0468503c664e50f7eab4b6"},{"name":"chocolate vermicelli","quantity":"1 Cup","subIngredients":"","_id":"5a0468503c664e50f7eab4b5"},{"name":"Big can Fruit salad","quantity":"1","subIngredients":"","_id":"5a0468503c664e50f7eab4b4"},{"name":"strawberry jam","quantity":"\\u00be Cup","subIngredients":"","_id":"5a0468503c664e50f7eab4b3"},{"name":"","quantity":"","subIngredients":"","_id":"5a0468503c664e50f7eab4b2"},{"name":"TOPPING CREAM","quantity":"bold","subIngredients":"","_id":"5a0468503c664e50f7eab4b1"},{"name":"dream whip","quantity":"2 Packs","subIngredients":"","_id":"5a0468503c664e50f7eab4b0"},{"name":"liquid milk","quantity":"1 Cup","subIngredients":"","_id":"5a0468503c664e50f7eab4af"},{"name":"grated chocolate","quantity":"100  Grams","subIngredients":"","_id":"5a0468503c664e50f7eab4ae"}]',
                'directions_legacy' => 'Cake:

&nbsp;
<ul>
<li>Beat the eggs, sugar, vanilla, and lemon rind with an electric mixer until it becomes light and fluffy.</li>
<li>Stir in the flour in two batches. Then, add the water, baking powder, lemon juice and cocoa.</li>
<li>Pour the preparation into a buttered tray.</li>
<li>Bake in the oven at 200 degrees C. for 45 to 50 minutes.</li>
</ul>
&nbsp;

Filling Cream:

&nbsp;
<ul>
<li>Mix egg, sugar, flour, vanilla, lemon rind and milk in a pot. Place the pot on the stove and stir until it thickens. Set aside.</li>
</ul>
&nbsp;

Topping Cream:

&nbsp;
<ul>
<li>Beat 2 packs of dream whip with one cup of cold milk in a bowl using an electric mixer until it becomes thick.</li>
</ul>
&nbsp;

Decoration:

&nbsp;
<ul>
<li>Drain the fruit salad and reserve the juice.</li>
<li>Split the cake horizontally into 3 layers.</li>
<li>Spread the strawberry jam on the first layer. Next, add half of the juice, the filling cream and fruit salad on the second layer. Cover with the third layer and pour the remaining juice. Spread the topping cream all over the cake. Sprinkle over the chocolate vermicelli or the grated chocolate.</li>
</ul>',
                'ingredients_legacy' => 'Cake:

&nbsp;
<ul>
<li>8 Eggs</li>
<li>2 Cups sugar</li>
<li>2 Cups flour</li>
<li>3 Tablespoons water</li>
<li>3 Tablespoons lemon juice</li>
<li>3 Teaspoons baking powder</li>
<li>1 Teaspoon grated lemon rind</li>
<li>1 Pinch vanilla</li>
<li>½ Cup cocoa</li>
</ul>
&nbsp;

Filling Cream:

&nbsp;
<ul>
<li>1 Egg</li>
<li>½ Cup sugar</li>
<li>2 Tablespoons flour</li>
<li>1 Cup liquid milk</li>
<li>1 Pinch vanilla</li>
<li>1 Teaspoon grated lemon rind</li>
<li>1 Big can Fruit salad</li>
<li>¾ Cup strawberry jam</li>
</ul>
&nbsp;

Topping cream:

&nbsp;
<ul>
<li>2 Packs Dream Whip</li>
<li>1 Cup liquid milk</li>
<li>100 Grams grated chocolate or 1 Cup chocolate vermicelli</li>
</ul>',
