                'ingredients' => '[{"name":"Figs","quantity":"1 Kg","subIngredients":"","_id":"59c8fc56f385c43042512ac0"},{"name":"Sugar","quantity":"4 Cups","subIngredients":"","_id":"59c8fc56f385c43042512abf"},{"name":"Water","quantity":"2 Cups","subIngredients":"","_id":"59c8fc56f385c43042512abe"},{"name":"Mandarin skin, sliced","quantity":"1","subIngredients":"","_id":"59c8fc56f385c43042512abd"},{"name":"peeled Almonds","quantity":"\\u00be  Cup","subIngredients":"","_id":"59c8fc56f385c43042512abc"},{"name":"Juice from 1 lemon","quantity":"","subIngredients":"","_id":"59c8fc56f385c43042512abb"}]',
